import React, { useEffect, useRef } from "react";
import axios from "axios";
import { SERVER_URL } from "../utils/URL";
import {
  Button,
  Form,
  Row,
  Col,
  Container,
  Alert,
  Tooltip,
  OverlayTrigger,
  Popover,
  Overlay,
} from "react-bootstrap";

function Login({ setUserData, setToken, tk }) {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [click, setClick] = React.useState(false);
  const [Rclick, setRclick] = React.useState(false);
  const [LoginError, setLoginError] = React.useState(false);
  const target = useRef(null);

  useEffect((e) => {
    console.log("hi");

    if (Rclick) window.location.href = "/register";
  });

  const onChangeEmail = (e) => {
    setEmail(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const handleRegister = () => {
    setRclick(true);
  };

  return (
    <Container
      className="justify-content-center align-self-center"
      style={{ height: "600px" }}
    >
      <Row
        className="justify-content-md-center "
        style={{ marginTop: "10rem" }}
      >
        <h1 style={{ marginBottom: "3rem" }}>Highteens Time</h1>
      </Row>
      <Row className="justify-content-md-center">
        <Col md={5}>
          <Form>
            <Form.Group controlId="formHorizontalEmail">
              <Col>
                <Form.Control
                  type="email"
                  placeholder="Email"
                  onChange={onChangeEmail}
                />
              </Col>
            </Form.Group>
            <Form.Group controlId="formHorizontalPassword">
              <Col>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  onChange={onChangePassword}
                />
              </Col>
            </Form.Group>
            <Form.Group>
              <Col>
                <Button
                  type="button"
                  disable={click}
                  style={{ width: "100%" }}
                  onClick={() => {
                    const data = {
                      email: email,
                      password: password,
                    };

                    axios
                      .post(SERVER_URL + "/login", data)
                      .then((res) => {
                        if (res.data === "failed") {
                          setLoginError(true);
                        } else {
                          localStorage.setItem("TOKEN", `Bearer ${res.data}`);

                          setLoginError(false);
                          setClick(true);
                          setToken(true);
                          setUserData(localStorage.getItem("TOKEN"));
                        }

                        //  history.push("/");
                      })
                      .catch((err) => {
                        console.log(err);
                      });
                  }}
                  ref={target}
                >
                  로그인
                </Button>
                <Overlay
                  target={target.current}
                  show={LoginError}
                  placement="right"
                >
                  {(props) => (
                    <Tooltip id="overlay-example" {...props}>
                      로그인 오류!<br></br>
                      <strong>이메일</strong> 또는 <strong>비밀번호</strong>를{" "}
                      <br />
                      확인해주세요.
                    </Tooltip>
                  )}
                </Overlay>

                <Button
                  type="button"
                  disable={Rclick}
                  style={{ width: "100%", marginTop: "0.3rem" }}
                  onClick={handleRegister}
                >
                  회원가입
                </Button>
              </Col>
            </Form.Group>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}

export default Login;
