import {
  BrowserRouter as Router,
  Redirect,
  Route,
  Switch,
} from "react-router-dom";
import Login from "./screens/Login";
import NotFound from "./screens/NotFound";

import styled from "styled-components";
import Register from "./screens/Register";
import Header from "./component/Header";
import Footer from "./component/Footer";
import Main from "./screens/Main";
import Logout from "./screens/Logout";
import UserInfo from "./screens/UserInfo";
import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { SERVER_URL } from "./utils/URL";
import { client, clientSet } from "./utils/ApiConfig";
import { Container, Row, Col } from "react-bootstrap";

function App() {
  const [userData, setUserData] = useState(null);
  const [tk, setToken] = useState(false);

  useEffect(client);

  console.log(userData);
  return (
    <Container fluid>
      <Router>
        {!localStorage.getItem("TOKEN") ? <Redirect to="/login" /> : null}
        {localStorage.getItem("TOKEN") ? (
          <Row>
            <Header data={userData} />
          </Row>
        ) : null}

        <Row>
          <Switch>
            <Route
              path="/"
              component={() => {
                <Main data={userData} />;
              }}
              exact
            />
            <Route path="/login" exact>
              <Login setUserData={setUserData} setToken={setToken} />
            </Route>
            <Route path="/logout" exact>
              <Logout />
            </Route>
            <Route path="/register" exact>
              <Register />
            </Route>
            <Route path="/user" exact>
              {!localStorage.getItem("TOKEN") ? (
                <Redirect to="/login"></Redirect>
              ) : (
                <UserInfo data={userData} />
              )}
            </Route>
            <Route>
              {!localStorage.getItem("TOKEN") ? (
                <Redirect to="/login"></Redirect>
              ) : (
                <NotFound />
              )}
            </Route>
          </Switch>
        </Row>
        <Row style={{ borderTop: "1px solid gray", marginTop: "2rem" }}>
          <Footer />
        </Row>
      </Router>
    </Container>
  );
}

export default App;
