import {
  BrowserRouter as Router,
  Redirect,
  Route,
  Switch,
} from "react-router-dom";
import Login from "./screens/Login";
import NotFound from "./screens/NotFound";

import styled from "styled-components";
import Register from "./screens/Register";
import Header from "./component/Header";
import Footer from "./component/Footer";
import Main from "./screens/Main";
import Logout from "./screens/Logout";
import UserInfo from "./screens/UserInfo";
import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { SERVER_URL } from "./utils/URL";
import { client, clientSet } from "./utils/ApiConfig";
import { Container, Row, Col } from "react-bootstrap";

function App() {
  const [userData, setUserData] = useState(null);
  const [tk, setToken] = useState(false);

  useEffect(client);

  console.log(userData);
  return (
    <Container fluid>
      <Router>
        {localStorage.getItem("TOKEN") ? (
          <Row>
            <Header data={userData} />
          </Row>
        ) : null}

        <Row>
          <Switch>
            <Route
              path="/"
              component={() => {
                if (!localStorage.getItem("TOKEN")) {
                  console.log("this is");
                  return <Redirect to="/login" />;
                } else {
                  return <Main data={userData} />;
                }
              }}
              exact
            />

            <Route
              path="/login"
              component={(props) => {
                return <Login props={props} />;
              }}
            />
            <Route
              path="/logout"
              component={() => {
                return <Logout />;
              }}
            />
            <Route
              path="/register"
              component={() => {
                return <Register />;
              }}
            />
            <Route
              path="/user"
              component={() => {
                if (!userData) {
                  return <Redirect to="/login" />;
                } else {
                  return <UserInfo data={userData} />;
                }
              }}
            />
            <Route>
              {!localStorage.getItem("TOKEN") ? (
                <Redirect to="/login"></Redirect>
              ) : (
                <NotFound />
              )}
            </Route>
          </Switch>
        </Row>
        <Row style={{ borderTop: "1px solid gray", marginTop: "2rem" }}>
          <Footer />
        </Row>
      </Router>
    </Container>
  );
}

export default App;
