import React, { useEffect, useRef } from "react";
import axios from "axios";
import { SERVER_URL } from "../utils/URL";
import {
  Button,
  Form,
  Row,
  Col,
  Container,
  Alert,
  Tooltip,
  OverlayTrigger,
  Popover,
  Overlay,
  InputGroup
} from "react-bootstrap";

function Register({ setUserData, setToken, tk }) {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [click, setClick] = React.useState(false);
  const [Rclick, setRclick] = React.useState(false);
  const [LoginError, setLoginError] = React.useState(false);
  const target = useRef(null);

  useEffect((e) => {
    console.log("hi");

    if (localStorage.getItem("TOKEN")) {
      window.location.href = "/";
    }
    if (Rclick) window.location.href = "/register";
  });

  const onChangeEmail = (e) => {
    setEmail(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const handleRegister = () => {
    setRclick(true);
  };

  return (
    <Container
      className="justify-content-center align-self-center"
      style={{ height: "600px" }}
    >
      <Row className="justify-content-md-center " style={{ marginTop: "3rem" }}>
        <h1 style={{ marginBottom: "3rem" }}>Highteens Time</h1>
      </Row>
      <Row className="justify-content-md-center">
        <Col md={5}>
          <Form>
            <Form.Group controlId="formHorizontalEmail">
              <Col>
                <Form.Control
                  type="email"
                  placeholder="Email"
                  onChange={onChangeEmail}
                />
              </Col>
            </Form.Group>
            <Form.Group controlId="formHorizontalPassword">
              <Col>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  onChange={onChangePassword}
                />
              </Col>
            </Form.Group>
            <InputGroup className="mb-3">
    <FormControl
      placeholder="Recipient's username"
      aria-label="Recipient's username"
      aria-describedby="basic-addon2"
    />
    <InputGroup.Append>
      <Button variant="outline-secondary">Button</Button>
    </InputGroup.Append>
  </InputGroup>

            <Form.Group>
              <Col>
                <Button
                  type="button"
                  disable={Rclick}
                  style={{ width: "100%", marginTop: "0.3rem" }}
                  onClick={handleRegister}
                >
                  회원가입
                </Button>
              </Col>
            </Form.Group>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}

export default Register;
