import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import { SERVER_URL } from "../utils/URL";
import {
  Button,
  Form,
  Row,
  Col,
  Container,
  Alert,
  Tooltip,
  OverlayTrigger,
  Popover,
  Overlay,
  InputGroup,
  FormControl,
} from "react-bootstrap";

function Register({ setUserData, setToken, tk }) {
  const [emailFront, setEmailFront] = useState(null);
  const [emailBack, setEmailBack] = useState(null);
  const [emailFull, setEmailFull] = useState(null);

  const [password, setPassword] = useState(null);

  const [username, setUsername] = useState(null);
  const [nickname, setNickname] = useState(null);

  const [region, setRegion] = useState(null);

  useEffect((e) => {
    console.log("hi");

    if (localStorage.getItem("TOKEN")) {
      window.location.href = "/";
    }
  });

  const onChangeUsername = (e) => {
    setUsername(e.target.value);
  };

  const onChangeNickname = (e) => {
    setNickname(e.target.value);
  };

  const onChangeEmailFront = (e) => {
    setEmailFront(e.target.value);
  };

  const onChangeEmailBack = (e) => {
    setEmailBack(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const onChangeRegion = (e) => {
    setRegion(e.target.value);
  };

  const handleRegister = () => {
    console.log();
  };

  return (
    <Container style={{ height: "600px" }} fluid>
      <Row style={{ marginTop: "3rem" }} className="justify-content-md-center">
        <Col md="auto">
          <h1 style={{ marginBottom: "3rem" }}>Highteens Time</h1>
        </Col>
      </Row>
      <Row className="justify-content-md-center">
        <Form style={{ width: "33%" }}>
          <Form.Row>
            <Col md={4}>
              <Form.Control
                type="text"
                placeholder="이름"
                onClick={onChangeUsername}
              />
            </Col>
            <Col md={5}>
              <Form.Control
                type="text"
                placeholder="닉네임"
                onClick={onChangeNickname}
              />
            </Col>
            <Col>
              <Button style={{ width: "100%" }}>닉네임 확인</Button>
            </Col>
          </Form.Row>
          <Form.Row style={{ marginTop: "0.5rem" }}>
            <Col md={4}>
              <Form.Label htmlFor="inlineFormInput" srOnly>
                Name
              </Form.Label>
              <Form.Control
                className="mb-2"
                id="inlineFormInput"
                placeholder="example"
                onChange={onChangeEmailFront}
              />
            </Col>
            <Col md={5}>
              <InputGroup className="mb-2">
                <InputGroup.Prepend>
                  <InputGroup.Text>@</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl
                  id="inlineFormInputGroup"
                  placeholder="example.com"
                  onChange={onChangeEmailBack}
                />
              </InputGroup>
            </Col>

            <Col md={3}>
              <Button
                type="button"
                className="mb-2"
                style={{ width: "100%" }}
                onClick={() => {
                  console.log("hi");
                }}
              >
                이메일 확인
              </Button>
            </Col>
          </Form.Row>
          <Form.Row style={{ marginTop: "0.0rem" }}>
            <Col>
              <Form.Control
                type="password"
                placeholder="Password"
                onChange={onChangePassword}
              />
            </Col>
          </Form.Row>
          <Form.Row style={{ marginTop: "0.5rem" }}>
            <Col>
              <Form.Control
                as="select"
                defaultValue="Choose..."
                onChange={onChangeRegion}
              >
                <option>지역 선택</option>
                <option>서울</option>
                <option>인천</option>
                <option>정읍</option>
                <option>익산</option>
              </Form.Control>
            </Col>
          </Form.Row>

          <Form.Row style={{ marginTop: "0.3rem" }}>
            <Col>
              <Button
                type="button"
                style={{ width: "100%", marginTop: "0.3rem" }}
                onClick={() => {
                  handleRegister;
                }}
              >
                회원가입
              </Button>
            </Col>
          </Form.Row>
        </Form>
      </Row>
    </Container>
  );
}

export default Register;
