import axios from "axios";
import Main from "../screens/Main";
import { SERVER_URL } from "./URL";

export const client = axios.create({
  headers: {
    Authorization: localStorage.getItem("TOKEN"),
  },
});

export const clientSet = (axios.defaults.headers.common["Authorization"] =
  localStorage.getItem("TOKEN"));

export const GetMessage = (props) => {
  const { data, setMessage } = props;
  axios.get(SERVER_URL + "/message/received", data).then(
    (res) => {
      setMessage(res.data);
    },
    (err) => {
      console.log(err);
    }
  );
};

export const SendMessage = (props) => {
  const { data, setMessage, email } = props;
  axios.post(SERVER_URL + `/message/${email}`, data).then(
    (res) => {},
    (err) => {
      console.log(err);
    }
  );
};

