import React, { useEffect, useRef } from "react";
import axios from "axios";
import { SERVER_URL } from "../utils/URL";
import {
  Button,
  Form,
  Row,
  Col,
  Container,
  Alert,
  Tooltip,
  OverlayTrigger,
  Popover,
  Overlay,
  InputGroup,
  FormControl,
} from "react-bootstrap";

function Register({ setUserData, setToken, tk }) {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");
  const [click, setClick] = React.useState(false);
  const [Rclick, setRclick] = React.useState(false);
  const [LoginError, setLoginError] = React.useState(false);
  const target = useRef(null);

  useEffect((e) => {
    console.log("hi");

    if (localStorage.getItem("TOKEN")) {
      window.location.href = "/";
    }
    if (Rclick) window.location.href = "/register";
  });

  const onChangeEmail = (e) => {
    setEmail(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const handleRegister = () => {
    setRclick(true);
  };

  return (
    <Container
      className="justify-content-center align-self-center"
      style={{ height: "600px" }}
    >
      <Row className="justify-content-md-center " style={{ marginTop: "3rem" }}>
        <h1 style={{ marginBottom: "3rem" }}>Highteens Time</h1>
      </Row>
      <Row className="justify-content-md-center">
        <Col md={8}>
          <Form>
            <Col style={{ offset: 2 }}>
              <Form.Label htmlFor="inlineFormInput" srOnly>
                Name
              </Form.Label>
              <Form.Control
                className="mb-2"
                id="inlineFormInput"
                placeholder="Jane Doe"
              />
            </Col>
            <Col>
              <Form.Label htmlFor="inlineFormInputGroup" srOnly>
                Username
              </Form.Label>
              <InputGroup className="mb-2">
                <InputGroup.Prepend>
                  <InputGroup.Text>@</InputGroup.Text>
                </InputGroup.Prepend>
                <FormControl id="inlineFormInputGroup" placeholder="Username" />
              </InputGroup>
            </Col>

            <Col>
              <Button type="submit" className="mb-2">
                Submit
              </Button>
            </Col>

            <Form.Group controlId="formHorizontalEmail">
              <Col>
                <Form.Control
                  type="email"
                  placeholder="Email"
                  onChange={onChangeEmail}
                />
              </Col>
            </Form.Group>
            <Form.Group controlId="formHorizontalPassword">
              <Col>
                <Form.Control
                  type="password"
                  placeholder="Password"
                  onChange={onChangePassword}
                />
              </Col>
            </Form.Group>

            <Form.Group>
              <Col>
                <Button
                  type="button"
                  disable={Rclick}
                  style={{ width: "100%", marginTop: "0.3rem" }}
                  onClick={handleRegister}
                >
                  회원가입
                </Button>
              </Col>
            </Form.Group>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}

export default Register;
